INSERT INTO `wpag_options` VALUES('10092', '_transient_dash_4077549d03da2e451c8b5f002294ff51', '<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/08/wordpress-4-2-4-security-and-maintenance-release/\'>WordPress 4.2.4 Security and Maintenance Release</a> <span class=\"rss-date\">August 4, 2015</span><div class=\"rssSummary\">WordPress 4.2.4 is now available. This is a security release for all previous versions and we strongly encourage you to update your sites immediately. This release addresses six issues, including three cross-site scripting vulnerabilities and a potential SQL injection that could be used to compromise a site, which were discovered by Marc-Alexandre Montpas of Sucuri, Helen Hou-Sandí [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://wptavern.com/the-wordpress-core-team-receives-praise-for-their-efforts-to-maintain-security\'>WPTavern: The WordPress Core Team Receives Praise for Their Efforts to Maintain Security</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/the-mantra-of-family-comes-first\'>WPTavern: The Mantra of Family Comes First</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/wordpress-4-2-4-patches-six-security-vulnerabilities\'>WPTavern: WordPress 4.2.4 Patches Six Security Vulnerabilities</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/ml-slider/\' class=\'dashboard-news-plugin-link\'>Meta Slider</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=ml-slider&amp;_wpnonce=01a8e8b8c1&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Meta Slider\'>Install</a>)</span></li></ul></div>', 'no');
