INSERT INTO `wpag_options` VALUES('13493', '_transient_dash_88ae138922fe95674369b1cb3d215a2b', '<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/09/wordpress-4-3-1/\'>WordPress 4.3.1 Security and Maintenance Release</a> <span class=\"rss-date\">September 15, 2015</span><div class=\"rssSummary\">WordPress 4.3.1 is now available. This is a security release for all previous versions and we strongly encourage you to update your sites immediately. This release addresses three issues, including two cross-site scripting vulnerabilities and a potential privilege escalation. WordPress versions 4.3 and earlier are vulnerable to a cross-site scripting vulnerability when processing shortcode tags (CVE-2015-5714). Reported by [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://ma.tt/2015/09/remembering-alex-king/\'>Matt: Remembering Alex King</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/alex-king-founder-of-crowd-favorite-passes-away\'>WPTavern: Alex King, Founder of Crowd Favorite Passes Away</a></li><li><a class=\'rsswidget\' href=\'https://poststatus.com/alex-king/\'>Post Status: Rest in peace, Alex King</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/ml-slider/\' class=\'dashboard-news-plugin-link\'>Meta Slider</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=ml-slider&amp;_wpnonce=5513f3adcc&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Meta Slider\'>Install</a>)</span></li></ul></div>', 'no');
