INSERT INTO `wpag_postmeta` VALUES('2678', '345', '_banner_title', 'field_5568d6cf238ec');
INSERT INTO `wpag_postmeta` VALUES('2679', '345', 'banner_text', 'AgilData provides a <strong>real-time data platform</strong> that helps companies to leverage Big Data by simplifying streaming at scale using SQL.\r\n\r\nAgilData was built for the Stock Market and today enables developers to:\r\n<ol>\r\n	<li><strong>Build</strong> massively scalable and extensible stream processing pipelines</li>\r\n	<li><strong>Interface</strong> cleanly with existing databases and message queues using standard SQL.</li>\r\n</ol>\r\nLed by industry database veterans, AgilData is a new paradigm for the way mission-critical applications and services are built and deployed, aligning Fast Data with the Agile Development principles of being lightweight, flexible, and incrementally scalable.');
INSERT INTO `wpag_postmeta` VALUES('2680', '345', '_banner_text', 'field_5568d6da238ed');
