INSERT INTO `wpag_postmeta` VALUES('2683', '345', 'section_1_content', '<ul class=\"usesList\">\r\n	<li>Risk Management</li>\r\n	<li>Fraud Detection</li>\r\n	<li>Internet of Things IOT</li>\r\n	<li>Market Data Management</li>\r\n	<li>Operational Intelligence</li>\r\n	<li>Data Warehouse Augmentation</li>\r\n	<li>Telecommunications</li>\r\n	<li>Dynamic Data Mart</li>\r\n	<li>E-commerce &amp; Personalization</li>\r\n	<li>Intelligence and Surveillance</li>\r\n	<li>Transaction and Algorithmic Analysis</li>\r\n	<li>Internet Security</li>\r\n	<li>Oil and Energy Sensor Data</li>\r\n	<li>ETL</li>\r\n</ul>');
INSERT INTO `wpag_postmeta` VALUES('2684', '345', '_section_1_content', 'field_5568d850840ad');
INSERT INTO `wpag_postmeta` VALUES('2685', '345', 'section_2_title', 'Why AgilData?');
INSERT INTO `wpag_postmeta` VALUES('2686', '345', '_section_2_title', 'field_5568de84a684e');
INSERT INTO `wpag_postmeta` VALUES('2687', '345', 'section_2_content', 'The Agile development movement revolutionized software development, driving <strong>down</strong> costs and <strong>shortening</strong> release cycles.   Features are developed more incrementally, embracing change, emphasizing collaboration over rigid processes. But these benefits stop at the data layer. Today\'s databases are too rigid and difficult to change.\r\n\r\nA truly Agile data platform tailors the data to each application need, handling everything from OLTP to real-time analytics to historical reporting. As application needs change, an Agile data platform must evolve gracefully along with changes to application requirements enabling the application developer and database administrators to work at the same pace.');
