INSERT INTO `wpag_postmeta` VALUES('4974', '518', 'section_1_content', '<ul class=\"usesList\">\r\n	<li>Risk Management</li>\r\n	<li>Fraud Detection</li>\r\n	<li>Internet of Things IOT</li>\r\n	<li>Market Data Management</li>\r\n	<li>Operational Intelligence</li>\r\n	<li>Data Warehouse Augmentation</li>\r\n	<li>Telecommunications</li>\r\n	<li>Finance</li>\r\n	<li>Dynamic Data Mart</li>\r\n	<li>E-commerce &amp; Personalization</li>\r\n	<li>Intelligence and Surveillance</li>\r\n	<li>Transaction and Algorithmic Analysis</li>\r\n	<li>Internet Security</li>\r\n	<li>Sensor Data</li>\r\n	<li>ETL</li>\r\n</ul>');
INSERT INTO `wpag_postmeta` VALUES('4975', '518', '_section_1_content', 'field_5568d850840ad');
INSERT INTO `wpag_postmeta` VALUES('4976', '518', 'section_2_title', 'Why AgilData?');
INSERT INTO `wpag_postmeta` VALUES('4977', '518', '_section_2_title', 'field_5568de84a684e');
INSERT INTO `wpag_postmeta` VALUES('4978', '518', 'section_2_content', 'The Agile development movement revolutionized software development driving <strong>down</strong> costs and <strong>shortening</strong> release cycles. Features are developed more incrementally embracing change and emphasizing collaboration over rigid processes. But these benefits <strong>stop</strong> at the data layer. Today\'s databases are too <strong>rigid</strong> and <strong>difficult</strong> to change.\r\n\r\nA truly <strong>Agile</strong> data platform tailors the data to each application need, handling everything from <strong>OLTP</strong>, to real-time <strong>analytics</strong>, to historical <strong>reporting</strong>. As application needs change, an Agile data platform must <strong>evolve</strong> gracefully to match the changes in applications enabling the application developer and data architects to deliver new features with ever greater <strong>impact</strong> to the business.');
INSERT INTO `wpag_postmeta` VALUES('4979', '518', '_section_2_content', 'field_5568de96a684f');
